import react, { useEffect, useState } from "react";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import 'bootstrap/dist/css/bootstrap.min.css';
import { getPokemons } from "../controller/getpokemon";
import { Pokemon } from "../models/pokemon.m";

const Listado = () => {

    const [pokemon, setPokemons] = useState<Pokemon[]>([]);
    useEffect(() => {
        const ObtenerTodos = async () => {
            const allPokemons = await getPokemons();
            setPokemons(allPokemons);
        }
        ObtenerTodos();
    });

    return (
        <>
            <h1>Pokemon - Nathy</h1>
            <div className="content-wrapper">
                <div className="content">
                    <div className="row gap-3">

                        {pokemons?.slice(0, 3).map((pokemon) => (
                            <Card className="mx-auto" style={{ width: '18rem' }}>
                                <Card.Header>Featured</Card.Header>
                                <Card.Img variant="top" width='80' height='100' className="d-block mx-auto w-50" src="https://img.pokemondb.net/sprites/black-white/anim/normal/bulbasaur.gif" />
                                <Card.Body>
                                    <Card.Title className="text-center">Bulbasaur</Card.Title>
                                    <ListGroup variant="flush">
                                        <ListGroup.Item>HP: </ListGroup.Item>
                                        <ListGroup.Item>Ataque: </ListGroup.Item>
                                        <ListGroup.Item>Defensa: </ListGroup.Item>
                                        <ListGroup.Item>E. Ataque: </ListGroup.Item>
                                        <ListGroup.Item>E. Defensa: </ListGroup.Item>
                                        <ListGroup.Item>Velocidad: ##</ListGroup.Item>
                                    </ListGroup>
                                </Card.Body>
                            </Card>
                        ))};
                    </div>
                </div>
            </div>
        </>
    )
}

export default Listado;
